using System;
using Server;
using Server.Gumps;
using Server.Network;
using Server.Commands;
using Server.Items;

namespace Server.Gumps
{
    public class OndeIrGump : Gump
    {
        Mobile caller;

        public static void Initialize()
        {
            CommandSystem.Register("ondeir", AccessLevel.Player, new CommandEventHandler(_OnCommand));
        }

        [Usage("ondeir")]
        [Description("Makes a call to your custom gump.")]
        public static void _OnCommand(CommandEventArgs e)
        {
            if (e.Mobile.HasGump(typeof(OndeIrGump)))
                e.Mobile.CloseGump(typeof(OndeIrGump));
            e.Mobile.SendGump(new OndeIrGump(e.Mobile));
        }

        public OndeIrGump(Mobile from) : this()
        {
            caller = from;
        }

        public OndeIrGump() : base(0, 0)
        {
            this.Closable = true;
            this.Disposable = true;
            this.Dragable = true;
            this.Resizable = false;

            AddPage(0);
            AddImage(447, 225, 30500);
            AddBackground(463, 236, 414, 100, 30536);
            AddHtml(594, 273, 165, 26, @"<CENTER>Encontre seu Destino</CENTER>", (bool)true, (bool)false);
            AddBackground(861, 213, 295, 484, 9390);
            AddImage(888, 254, Utility.Random(30567, 15));
            AddButton(541, 602, 2151, 2151, (int)Buttons.NPCS, GumpButtonType.Reply, 0);
            AddHtml(483, 573, 152, 23, @"<CENTER>NPCS</CENTER>", (bool)true, (bool)false);
            AddHtml(704, 572, 152, 23, @"<CENTER>AVENTURAS</CENTER>", (bool)true, (bool)false);
            AddButton(763, 600, 2151, 2151, (int)Buttons.Aventuras, GumpButtonType.Reply, 0);
            AddHtml(481, 343, 152, 23, @"<CENTER>LOCAIS</CENTER>", (bool)true, (bool)false);
            AddButton(527, 373, 2151, 2151, (int)Buttons.Locais, GumpButtonType.Reply, 0);

            AddHtml(700, 343, 152, 23, @"<CENTER>PARAR BUSCA</CENTER>", (bool)true, (bool)false);
            AddButton(763, 369, 2151, 2151, (int)Buttons.Parar, GumpButtonType.Reply, 0);

            AddImage(654, 427, 39830);
        }

        public enum Buttons
        {
            NPCS = 1,
            Aventuras = 2,
            Locais = 3,
            Parar = 4
        }


        public override void OnResponse(NetState sender, RelayInfo info)
        {
            Mobile from = sender.Mobile;

            switch (info.ButtonID)
            {
                case (int)Buttons.NPCS:
                    {
                        from.CloseAllGumps();
                        from.SendGump(new NPCSGump(from));
                        break;
                    }
                case (int)Buttons.Aventuras:
                    {
                        from.CloseAllGumps();
                        from.SendGump(new MissoesGump(from));
                        break;
                    }
                case (int)Buttons.Locais:
                    {
                        from.SendMessage("Em Breve...");
                        break;
                    }
                case (int)Buttons.Parar:
                    {
                        if (from.QuestArrow != null)
                        {
                            from.QuestArrow.Stop();
                            from.QuestArrow = null;
                            from.SendMessage("Voce parou sua busca");
                        }
                        else
                        {
                            from.SendMessage("Voce nao esta buscando nada");
                        }
                        break;
                    }
            }
        }
    }
}
